import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchUsers } from '../../redux/actions/userActions';
import { Box, Card, CardContent, CardMedia, Typography, IconButton } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const UserList = () => {
  const dispatch = useDispatch();
  const { users, loading, error } = useSelector((state) => state.user);

  useEffect(() => {
    dispatch(fetchUsers());
  }, [dispatch]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <Box
      sx={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
        gap: 2,
        padding: 2,
      }}
    >
      {users.map((user) => (
        <Card key={user.id} sx={{ maxWidth: 345, position: 'relative', borderRadius: 2 }}>
          <CardMedia
            component="img"
            height="200"
            image={user.avatar}
            alt={`${user.first_name} ${user.last_name}`}
          />
          <CardContent>
            <Typography variant="h6" component="div" align="center">
              {user.first_name} {user.last_name}
            </Typography>
            <Typography variant="body2" color="text.secondary" align="center">
              {user.email}
            </Typography>
          </CardContent>
          <Box
            sx={{
              position: 'absolute',
              bottom: 8,
              right: 8,
              display: 'flex',
              gap: 1,
            }}
          >
            <IconButton color="primary" onClick={() => alert(`Edit ${user.first_name}`)}>
              <EditIcon />
            </IconButton>
            <IconButton color="error" onClick={() => alert(`Delete ${user.first_name}`)}>
              <DeleteIcon />
            </IconButton>
          </Box>
        </Card>
      ))}
    </Box>
  );
};

export default UserList;
