import React, { useState, useEffect } from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, DialogActions, Button } from '@mui/material';

const UserModal = ({ open, handleClose, user, handleSave }) => {
  const [userData, setUserData] = useState({ first_name: '', last_name: '', email: '', avatar: '' });

  useEffect(() => {
    if (user) {
      setUserData(user); // Pre-fill with user data if editing
    } else {
      setUserData({ first_name: '', last_name: '', email: '', avatar: '' }); // Empty fields for creating
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value }); // Update local state
  };

  const handleSubmit = () => {
    console.log('Submitting user data:', userData);
    handleSave(userData); // Pass the user data to the parent save handler
    handleClose(); // Close the modal
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>{user ? 'Edit User' : 'Create User'}</DialogTitle>
      <DialogContent>
        <TextField
          margin="dense"
          name="first_name"
          label="First Name"
          fullWidth
          value={userData.first_name}
          onChange={handleChange}
        />
        <TextField
          margin="dense"
          name="last_name"
          label="Last Name"
          fullWidth
          value={userData.last_name}
          onChange={handleChange}
        />
        <TextField
          margin="dense"
          name="email"
          label="Email Address"
          fullWidth
          value={userData.email}
          onChange={handleChange}
        />
        <TextField
          margin="dense"
          name="avatar"
          label="Avatar URL"
          fullWidth
          value={userData.avatar}
          onChange={handleChange}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose}>Cancel</Button>
        <Button onClick={handleSubmit} variant="contained">
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default UserModal;
