import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchUsers, removeUser } from '../redux/actions/userActions';
import UserList from '../components/Users/UserList';
import UserCardView from '../components/Users/UserCardView';
import UserModal from '../components/Users/UserModal';
import ViewToggle from '../components/Users/ViewToggle';
import { Button, Box, Typography } from '@mui/material';

const UserManagementPage = () => {
  const dispatch = useDispatch();
  const { users, loading, error, currentPage } = useSelector((state) => state.user);

  const [viewMode, setViewMode] = useState('table'); // 'table' or 'card'
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  // Fetch users only once on component mount
  useEffect(() => {
    dispatch(fetchUsers(currentPage)); // Initial fetch
  }, [dispatch, currentPage]); // Ensure dependencies are correct

  const handleCreate = () => {
    setSelectedUser(null);
    setIsModalOpen(true);
  };

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      dispatch(removeUser(id));
    }
  };

  if (loading) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error: {error}</Typography>;

  return (
    <Box sx={{ padding: 2 }}>
      {/* Header Section */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">Users</Typography>
        <Button variant="contained" color="primary" onClick={handleCreate}>
          Create User
        </Button>
      </Box>

      {/* View Toggle */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
        <ViewToggle viewMode={viewMode} setViewMode={setViewMode} />
      </Box>

      {/* Content Section */}
      {viewMode === 'table' ? (
        <UserList users={users} onEdit={handleEdit} onDelete={handleDelete} />
      ) : (
        <UserCardView users={users} onEdit={handleEdit} onDelete={handleDelete} />
      )}

      {/* User Modal */}
      <UserModal
        open={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        user={selectedUser}
      />
    </Box>
  );
};

export default UserManagementPage;
