import React from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../../api/userApi';
import { Box, Button, TextField, Typography, Container, CssBaseline, Alert } from '@mui/material';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

// Validation schema using Yup
const validationSchema = Yup.object({
  email: Yup.string()
    .email('Invalid email address')
    .required('Email is required'),
  password: Yup.string()
    .min(6, 'Password must be at least 6 characters')
    .required('Password is required'),
});

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async (values, { setSubmitting, setErrors }) => {
    try {
      const response = await login(values);
      localStorage.setItem('token', response.data.token);
      navigate('/users');
    } catch (error) {
      // Safely log error details without triggering circular reference
      console.error('Login Error:', error.response?.data || error.message);

      // Display a user-friendly error message
      setErrors({ apiError: error.response?.data?.error || 'An unexpected error occurred' });
    }
    setSubmitting(false);
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Typography component="h1" variant="h5">
          Login
        </Typography>
        <Formik
          initialValues={{ email: '', password: '' }}
          validationSchema={validationSchema}
          onSubmit={handleLogin}
        >
          {({ isSubmitting, errors }) => (
            <Form>
              {/* Display API Error */}
              {errors.apiError && (
                <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
                  {errors.apiError}
                </Alert>
              )}

              {/* Email Field */}
              <Field
                as={TextField}
                name="email"
                label="Email Address"
                fullWidth
                margin="normal"
                error={!!errors.email}
                helperText={<ErrorMessage name="email" />}
                autoComplete="email"
              />

              {/* Password Field */}
              <Field
                as={TextField}
                name="password"
                label="Password"
                type="password"
                fullWidth
                margin="normal"
                error={!!errors.password}
                helperText={<ErrorMessage name="password" />}
                autoComplete="current-password"
              />

              {/* Submit Button */}
              <Button
                type="submit"
                fullWidth
                variant="contained"
                disabled={isSubmitting}
                sx={{ mt: 3, mb: 2 }}
              >
                Log In
              </Button>
            </Form>
          )}
        </Formik>
      </Box>
    </Container>
  );
};

export default Login;
