import { fetchUsersRequest, fetchUsersSuccess, fetchUsersFailure } from '../reducers/userReducer';
import { getUsers, createUser, updateUser, deleteUser } from '../../api/userApi';

// Fetch Users
export const fetchUsers = (page = 1) => async (dispatch) => {
  dispatch({ type: 'FETCH_USERS_REQUEST' });
  try {
    const response = await getUsers(page); // API call
    dispatch({
      type: 'FETCH_USERS_SUCCESS',
      payload: { data: response.data.data, totalPages: response.data.total_pages },
    });
  } catch (error) {
    dispatch({ type: 'FETCH_USERS_FAILURE', payload: error.message });
  }
};


// Create User
export const addUser = (userData) => async (dispatch) => {
  try {
    const response = await createUser(userData);
    dispatch(fetchUsersSuccess(response.data));
  } catch (error) {
    console.error(error);
  }
};

// Update User
export const editUser = (id, userData) => async (dispatch) => {
  try {
    const response = await updateUser(id, userData);
    dispatch(fetchUsersSuccess(response.data));
  } catch (error) {
    console.error(error);
  }
};

// Delete User
export const removeUser = (id) => async (dispatch) => {
  try {
    await deleteUser(id);
    dispatch(fetchUsersSuccess(id));
  } catch (error) {
    console.error(error);
  }
};
