const initialState = {
  users: [],
  currentPage: 1,
  totalPages: 1,
  loading: false,
  error: null,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'FETCH_USERS_SUCCESS':
      console.log('Reducer - FETCH_USERS_SUCCESS:', action.payload); // Debug log
      return {
        ...state,
        loading: false,
        users: action.payload.data,
        totalPages: action.payload.totalPages,
        currentPage: action.payload.page,
      };
    default:
      return state;
  }
};


export default userReducer;
