import { getUsers, createUser, updateUser, deleteUser } from '../../api/userApi';

// Action Creators
export const fetchUsersRequest = () => ({ type: 'FETCH_USERS_REQUEST' });
export const fetchUsersSuccess = (payload) => ({ type: 'FETCH_USERS_SUCCESS', payload });
export const fetchUsersFailure = (error) => ({ type: 'FETCH_USERS_FAILURE', payload: error });

// Fetch Users
export const fetchUsers = (page = 1) => async (dispatch) => {
  dispatch(fetchUsersRequest());
  try {
    const response = await getUsers(page);
    console.log('Fetch Users API Response:', response.data); // Debug log
    dispatch(
      fetchUsersSuccess({
        data: response.data.data,
        totalPages: response.data.total_pages,
        page: response.data.page,
      })
    );
  } catch (error) {
    console.error('Fetch Users Error:', error.message); // Debug log
    dispatch(fetchUsersFailure(error.message));
  }
};

// Create User
export const addUser = (userData) => async (dispatch) => {
  try {
    const response = await createUser(userData);
    console.log('Create User API Response:', response.data); // Debug
    dispatch({
      type: 'CREATE_USER',
      payload: response.data, // Add the newly created user to the state
    });
  } catch (error) {
    console.error('Create User Error:', error.message); // Debug
  }
};

export const editUser = (id, userData) => async (dispatch) => {
  try {
    const response = await updateUser(id, userData);
    console.log('Update User API Response:', response.data); // Debug
    dispatch({
      type: 'UPDATE_USER',
      payload: { ...response.data, id }, // Ensure the `id` is included
    });
  } catch (error) {
    console.error('Update User Error:', error.message); // Debug
  }
};



export const removeUser = (id) => async (dispatch) => {
  try {
    console.log('Deleting user:', id); // Debug log
    await deleteUser(id);
    dispatch({
      type: 'DELETE_USER',
      payload: id, // Remove the user from the state
    });
  } catch (error) {
    console.error('Delete User Error:', error.message); // Debug log
  }
};
