import { getUsers, createUser, updateUser, deleteUser } from '../../api/userApi';

// Action Creators
export const fetchUsersRequest = () => ({
  type: 'FETCH_USERS_REQUEST',
});

export const fetchUsersSuccess = (payload) => ({
  type: 'FETCH_USERS_SUCCESS',
  payload,
});

export const fetchUsersFailure = (error) => ({
  type: 'FETCH_USERS_FAILURE',
  payload: error,
});

// Fetch Users
export const fetchUsers = (page = 1) => async (dispatch) => {
  dispatch(fetchUsersRequest());
  console.log('Fetching users for page:', page); // Debug log
  try {
    const response = await getUsers(page);
    console.log('API Response:', response.data); // Debug log
    dispatch(fetchUsersSuccess({
      data: response.data.data, // Adjust this based on actual response
      totalPages: response.data.total_pages,
      page: response.data.page,
    }));
  } catch (error) {
    console.error('Fetch Users Error:', error.message); // Debug log
    dispatch(fetchUsersFailure(error.message));
  }
};

// Create User
export const addUser = (userData) => async (dispatch) => {
  try {
    console.log('Adding user:', userData); // Debug log
    await createUser(userData);
    dispatch(fetchUsers()); // Refetch users after adding
  } catch (error) {
    console.error('Add User Error:', error.message); // Debug log
  }
};

// Update User
export const editUser = (id, userData) => async (dispatch) => {
  try {
    console.log('Editing user:', id, userData); // Debug log
    await updateUser(id, userData);
    dispatch(fetchUsers()); // Refetch users after updating
  } catch (error) {
    console.error('Edit User Error:', error.message); // Debug log
  }
};

// Delete User
export const removeUser = (id) => async (dispatch) => {
  try {
    console.log('Deleting user:', id); // Debug log
    await deleteUser(id);
    dispatch(fetchUsers()); // Refetch users after deleting
  } catch (error) {
    console.error('Delete User Error:', error.message); // Debug log
  }
};
