import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchUsers, addUser, editUser, removeUser } from '../redux/actions/userActions';
import UserList from '../components/Users/UserList';
import UserCardView from '../components/Users/UserCardView';
import UserModal from '../components/Users/UserModal';
import UserSearch from '../components/Users/UserSearch';
import UserPagination from '../components/Users/UserPagination';
import { Box, Typography, Button, ToggleButton, ToggleButtonGroup } from '@mui/material';

const UserManagementPage = () => {
  const dispatch = useDispatch();
  const { users, loading, error, currentPage, totalPages } = useSelector((state) => state.user);

  const [viewMode, setViewMode] = useState('table'); // View mode toggle
  const [openModal, setOpenModal] = useState(false); // Modal state
  const [currentUser, setCurrentUser] = useState(null); // User being edited/created
  const [searchTerm, setSearchTerm] = useState(''); // Search state

  useEffect(() => {
    dispatch(fetchUsers(currentPage));
  }, [dispatch, currentPage]);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      dispatch(removeUser(id));
    }
  };

  const handleEdit = (user) => {
    setCurrentUser(user);
    setOpenModal(true);
  };

  const handleCreate = () => {
    setCurrentUser(null);
    setOpenModal(true);
  };

  const handleSave = (userData) => {
    if (currentUser) {
      // Editing existing user
      dispatch(editUser(currentUser.id, userData));
    } else {
      // Creating new user
      dispatch(addUser(userData));
    }
  };

  const handleSearch = () => {
    return users.filter(
      (user) =>
        user.first_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.last_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setCurrentUser(null);
  };

  if (loading) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error: {error}</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h4">Users</Typography>
        <Button variant="contained" color="primary" onClick={handleCreate}>
          Create User
        </Button>
      </Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <UserSearch searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
        <ToggleButtonGroup
          value={viewMode}
          exclusive
          onChange={(e, value) => setViewMode(value)}
          aria-label="view mode"
        >
          <ToggleButton value="table" aria-label="table view">
            Table
          </ToggleButton>
          <ToggleButton value="card" aria-label="card view">
            Card
          </ToggleButton>
        </ToggleButtonGroup>
      </Box>
      {viewMode === 'table' ? (
        <UserList users={handleSearch()} onEdit={handleEdit} onDelete={handleDelete} />
      ) : (
        <UserCardView users={handleSearch()} onEdit={handleEdit} onDelete={handleDelete} />
      )}
      <UserPagination
        totalPages={totalPages}
        currentPage={currentPage}
        setPage={(page) => dispatch(fetchUsers(page))}
      />
      <UserModal open={openModal} handleClose={handleCloseModal} user={currentUser} handleSave={handleSave} />
    </Box>
  );
};

export default UserManagementPage;
