import React, { useState } from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  Container,
  Box,
  TextField,
  Button,
  Typography,
  CssBaseline,
  Alert,
} from '@mui/material';
import { sendPasswordReset } from '../../api/userApi';

const ForgotPassword = () => {
  const [successMessage, setSuccessMessage] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      await sendPasswordReset(values.email);
      setSuccessMessage('Password reset instructions have been sent to your email.');
      setErrorMessage(null);
    } catch (error) {
      setErrorMessage('Failed to send password reset instructions.');
      setSuccessMessage(null);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Typography component="h1" variant="h5">
          Forgot Password
        </Typography>
        <Formik
          initialValues={{ email: '' }}
          validationSchema={Yup.object({
            email: Yup.string()
              .email('Invalid email address')
              .required('Email is required'),
          })}
          onSubmit={handleSubmit}
        >
          {({ isSubmitting, errors, touched }) => (
            <Form>
              {/* Success Message */}
              {successMessage && (
                <Alert severity="success" sx={{ width: '100%', mb: 2 }}>
                  {successMessage}
                </Alert>
              )}

              {/* Error Message */}
              {errorMessage && (
                <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
                  {errorMessage}
                </Alert>
              )}

              <Field
                as={TextField}
                name="email"
                label="Email Address"
                fullWidth
                margin="normal"
                autoComplete="email"
                error={touched.email && !!errors.email} // Show red border only when field is touched
                helperText={touched.email && errors.email} // Show error message only when field is touched
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                disabled={isSubmitting}
                sx={{ mt: 3, mb: 2 }}
              >
                Send Reset Instructions
              </Button>
            </Form>
          )}
        </Formik>
      </Box>
    </Container>
  );
};

export default ForgotPassword;
