import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchUsers, addUser, editUser, removeUser } from '../redux/actions/userActions';
import UserList from '../components/Users/UserList';
import UserCardView from '../components/Users/UserCardView';
import UserModal from '../components/Users/UserModal';
import UserPagination from '../components/Users/UserPagination';
import UserSearch from '../components/Users/UserSearch';
import ViewToggle from '../components/Users/ViewToggle';
import { Button, Box, Typography } from '@mui/material';

const UserManagementPage = () => {
  const dispatch = useDispatch();
  const { users, loading, error, currentPage, totalPages } = useSelector((state) => state.user);

  const [viewMode, setViewMode] = useState('table'); // 'table' or 'card'
  const [searchTerm, setSearchTerm] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  // Fetch users on mount or page change
  useEffect(() => {
    dispatch(fetchUsers(currentPage));
  }, [dispatch, currentPage]);

  // Filter users by search term
  const filteredUsers = users.filter((user) =>
    `${user.first_name} ${user.last_name}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Open modal for creating or editing users
  const handleCreate = () => {
    setSelectedUser(null);
    setIsModalOpen(true);
  };

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleSave = (userData) => {
    if (selectedUser) {
      dispatch(editUser(selectedUser.id, userData));
    } else {
      dispatch(addUser(userData));
    }
    setIsModalOpen(false);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      dispatch(removeUser(id));
    }
  };

  const setPage = (page) => {
    dispatch(fetchUsers(page));
  };

  if (loading) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error: {error}</Typography>;

  return (
    <Box sx={{ padding: 2 }}>
      {/* Header Section */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">Users</Typography>
        <Button variant="contained" color="primary" onClick={handleCreate}>
          Create User
        </Button>
      </Box>

      {/* Search & View Toggle */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
        <UserSearch searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
        <ViewToggle viewMode={viewMode} setViewMode={setViewMode} />
      </Box>

      {/* User Content */}
      {viewMode === 'table' ? (
        <UserList users={filteredUsers} onEdit={handleEdit} onDelete={handleDelete} />
      ) : (
        <UserCardView users={filteredUsers} onEdit={handleEdit} onDelete={handleDelete} />
      )}

      {/* Pagination */}
      <UserPagination
        totalPages={totalPages}
        currentPage={currentPage}
        setPage={setPage}
      />

      {/* User Modal */}
      <UserModal
        open={isModalOpen}
        handleClose={() => setIsModalOpen(false)}
        user={selectedUser}
        handleSave={handleSave}
      />
    </Box>
  );
};

export default UserManagementPage;
