import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchUsers, removeUser } from '../redux/actions/userActions';
import UserList from '../components/Users/UserList';
import UserPagination from '../components/Users/UserPagination';
import { Box, Typography, Button } from '@mui/material';

const UserManagementPage = () => {
  const dispatch = useDispatch();
  const { users, loading, error, currentPage, totalPages } = useSelector((state) => state.user);

  useEffect(() => {
    dispatch(fetchUsers(currentPage)); // Fetch users for the current page
  }, [dispatch, currentPage]);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      dispatch(removeUser(id)); // Dispatch delete user action
    }
  };

  const setPage = (page) => {
    dispatch(fetchUsers(page)); // Update the page and fetch users
  };

  if (loading) return <Typography>Loading...</Typography>;
  if (error) return <Typography color="error">Error: {error}</Typography>;

  return (
    <Box sx={{ padding: 2 }}>
      <Typography variant="h4">Users</Typography>
      <Button variant="contained" color="primary" sx={{ marginBottom: 2 }}>
        Create User
      </Button>
      <UserList users={users} onDelete={handleDelete} />
      <UserPagination totalPages={totalPages} currentPage={currentPage} setPage={setPage} />
    </Box>
  );
};

export default UserManagementPage;
