import React, { useEffect } from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, DialogActions, Button } from '@mui/material';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

// Validation Schema using Yup
const validationSchema = Yup.object().shape({
  first_name: Yup.string().required('First Name is required'),
  last_name: Yup.string().required('Last Name is required'),
  email: Yup.string().email('Invalid email address').required('Email is required'),
  avatar: Yup.string()
    .url('Invalid URL format')
    .matches(/\.(jpg|jpeg|png|gif)$/, 'Must be an image URL (.jpg, .jpeg, .png, .gif)')
    .required('Avatar URL is required'),
});

const UserModal = ({ open, handleClose, user, handleSave }) => {
  // Default initial values for the form
  const initialValues = {
    first_name: user?.first_name || '',
    last_name: user?.last_name || '',
    email: user?.email || '',
    avatar: user?.avatar || '',
  };

  const handleSubmit = (values, { setSubmitting }) => {
    console.log('Submitting user data:', values);
    handleSave(values); // Pass the user data to the parent save handler
    handleClose(); // Close the modal
    setSubmitting(false); // Stop the submit loading state
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>{user ? 'Edit User' : 'Create User'}</DialogTitle>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
        enableReinitialize // Ensures form reinitializes when `user` changes
      >
        {({ isSubmitting, errors, touched }) => (
          <Form>
            <DialogContent>
              <Field
                name="first_name"
                as={TextField}
                margin="dense"
                label="First Name"
                fullWidth
                error={touched.first_name && Boolean(errors.first_name)}
                helperText={<ErrorMessage name="first_name" />}
              />
              <Field
                name="last_name"
                as={TextField}
                margin="dense"
                label="Last Name"
                fullWidth
                error={touched.last_name && Boolean(errors.last_name)}
                helperText={<ErrorMessage name="last_name" />}
              />
              <Field
                name="email"
                as={TextField}
                margin="dense"
                label="Email Address"
                fullWidth
                error={touched.email && Boolean(errors.email)}
                helperText={<ErrorMessage name="email" />}
              />
              <Field
                name="avatar"
                as={TextField}
                margin="dense"
                label="Avatar URL"
                fullWidth
                error={touched.avatar && Boolean(errors.avatar)}
                helperText={<ErrorMessage name="avatar" />}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose} disabled={isSubmitting}>
                Cancel
              </Button>
              <Button type="submit" variant="contained" disabled={isSubmitting}>
                {isSubmitting ? 'Saving...' : 'Save'}
              </Button>
            </DialogActions>
          </Form>
        )}
      </Formik>
    </Dialog>
  );
};

export default UserModal;
